package arrayPackage;

public class Array_1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int[] array = {1,2,3,4,5};
		
		int length = array.length;
		
		for(int i = 0; i < length; i++) {
			System.out.print(array[i]+ " ");
		}
		System.out.println();

	}

}
